document.addEventListener("DOMContentLoaded",(()=>{document.querySelector(".profile-arrow-button").addEventListener("click",(function(){this.querySelector(".arrow-select-down").classList.toggle("rotated")}));const e=document.querySelectorAll(".filter-tab");e.forEach((t=>{t.addEventListener("click",(function(c){c.preventDefault(),e.forEach((e=>e.classList.remove("active"))),t.classList.add("active")}))}))}));